# convert to list and enumerate
steps = list(enumerate(steps))

# starting position of 1 - returned incorrect answer - changed to 1 and it was correct. why?
floor = 1

# assign each bracket to a variable
up = "("
down = ")"

# iterates through steps, adds 1 for each step up and down for each step down.
# breaks when the floor is equal to -1
for count, value in steps:
    if value == up:
        floor += 1
    elif value == down:
        floor -= 1
    if floor == -1:
        break

# prints where the counter got to before reaching floor -1
print(count)
